package Analyzer;import java.util.stream.Stream;public class NumberOfWordsAnalyzer implements Analyzer<Integer> {    @Override    public Integer analyze(String text) {        String [] parseText = text.split(" ");         long count = Stream.of(parseText)                 .filter(x-> {            char[] tempArr = x.toCharArray();            for(int i = 0;i < tempArr.length; i++){                if(Character.isLetter(tempArr[i])){                return true;}            }            return false;        })                .count();        Integer intCount = (int) (long) count;        return intCount;    }}