import Analyzer.Analyzer;import Analyzer.CharFrequencyAnalyzer;import Analyzer.LanguageRecognition;import Analyzer.NumberOfWordsAnalyzer;import java.util.Arrays;import java.util.List;public class MultiAnalizer {    public static void callAnalyzers(String text) {        List<Analyzer> analyzers = Arrays.asList(                new CharFrequencyAnalyzer(),                new NumberOfWordsAnalyzer(),                new LanguageRecognition());        for (Analyzer analyzer : analyzers) {            System.out.println(analyzer.analyze(text));            System.out.println("-------");        }    }}