package Analyzer;import org.junit.Test;import java.util.HashMap;import java.util.Map;import static org.junit.Assert.assertEquals;import static org.junit.Assert.assertTrue;public class CharFrequencyAnalyzerTest {    @Test    public void analyzeTest(){        CharFrequencyAnalyzer charFrequencyAnalyzer = new CharFrequencyAnalyzer();        String testText = "q w e r t y u i  q w";        Map<Character,Double> actualMap = charFrequencyAnalyzer.analyze(testText);        Map<Character,Double> expectedMap = new HashMap<>();        expectedMap.put('q',0.2);        expectedMap.put('w',0.2);        expectedMap.put('e',0.1);        expectedMap.put('r',0.1);        expectedMap.put('t',0.1);        expectedMap.put('y',0.1);        expectedMap.put('u',0.1);        expectedMap.put('i',0.1);        assertTrue(expectedMap.equals(actualMap));    }}////        String test = "a bb ccc bb a";//        Map<Character,Double> testMap = new HashMap<>();//        testMap.put('a',0.1538); testMap.put('b',0.3077); testMap.put(' ',0.3077); testMap.put('c',0.2308);//        assertEquals(testMap, new CharFrequencyAnalyzer().analyze(test));//    }