package Analyzer;import org.junit.Test;import java.util.Arrays;import static org.junit.Assert.assertArrayEquals;import static org.junit.Assert.assertTrue;public class MostFrequentWordsAnalyzerTest {    @Test    public void testAnalyzeAllTen() {        MostFrequentWordsAnalyzer mostFrequentWordsAnalyzer = new MostFrequentWordsAnalyzer();        String textForTest = "a, a b b c c d d f, f. g g w w q q? e e r r t " +                "y i u o p";        String[] freqencyActual = mostFrequentWordsAnalyzer.analyze(textForTest);        String[] expectted = {"q", "a", "r", "b", "c", "d", "e", "f", "w", "g"};        assertTrue(Arrays.equals(expectted, freqencyActual));    }    @Test    public void testAnalyzeHalfData(){        MostFrequentWordsAnalyzer mostFrequentWordsAnalyzer = new MostFrequentWordsAnalyzer();        String textForTest = "a a b b c c d d f f  t " +                "y i u o p";        String[] freqencyActual = mostFrequentWordsAnalyzer.analyze(textForTest);        String[] expectted = {"a", "b", "c", "d", "f"};        assertTrue(Arrays.equals(expectted, freqencyActual));    }}   // String test = "a bb ccc bb a";//        String[] testArray = {"a","bb","ccc"};//        assertArrayEquals(testArray, new MostFrequentWordsAnalyzer().analyze(test));